const http = require('http');
var fs = require('fs');
const express = require('express');
const bodyParser = require('body-parser');
const concat = require('concat-stream');
var qs = require('querystring');
//var beep = require('beepbeep');

let data = {
  '28-0000097807f2': 75.4,
  '28-01205fb628b9': 65.2,
  '28-000009797137': 76.1,
  '28-000009798d37': 65.8,
};

let dataMap = new Array();
console.log(dataMap);
var intevalMs = 5000;
var logHour = 5;
var logSize = 3;

function reloadLogSize()
{
  logSize = (logHour * 60 * 60 * 1000) / intevalMs;
}

reloadLogSize();

setInterval(() => {
  // Update the data with random temperatures for each sensor
  for (const key in data) {
    data[key] = Math.floor(Math.random() * 50) + 10;
  }
  //process.stdout.write('\u0007');

  const time = { "time": new Date().toLocaleString() };
  const merged = { ...time, ...data };

  dataMap.push(merged);
  while (dataMap.length > logSize)
  {
    dataMap.shift();
  }
  //console.log(dataMap);

}, intevalMs);

//let audio = new Audio('bell.mp3');

fs.readFile('./index.html', function (err, html) {
  if (err) throw err;

  const server = http.createServer(function (req, res) {
    console.log(req.url);

    if (req.url === '/') {
      res.writeHeader(200, { 'Content-Type': 'text/html' });
      res.write(html);
      res.end();
    } else if (req.url === '/data') {
      res.writeHead(200, { 'Content-Type': 'application/json' });
      res.write(JSON.stringify(data));
      res.end();
    } else {
      res.writeHead(404, { 'Content-Type': 'text/plain' });
      res.write('404 Not Found');
      res.end();
    }
  });

  server.listen(80, () => {
    console.log('Server started on port 80');
  });
});


const app = express();
const PORT = 3000;

app.use(bodyParser.urlencoded({ extended: true }));
app.use(express.static('public')); // Serve static files (e.g., HTML, CSS, JS) from the 'public' directory

app.post('/process', (req, res) => {
  const quantity = req.body.quantity || 'N/A';
  console.log(quantity);
  res.send(`Received quantity: ${quantity}`);
});

app.listen(PORT, () => {
  console.log(`Server running at http://localhost:${PORT}`);
});

